#################################################

    
 update risk_factors a
join (select min(factorname) as factorname, factor_id from risk_factors group by factor_id) as b on (a.factor_id=b.factor_id)
set a.factorname=b.factorname;
  update sub_type_codes a
join (select min(group_name) as group_name, group_id from sub_type_codes group by group_id) as b on (a.group_id=b.group_id)
set a.group_name=b.group_name;

  update sub_type_codes a
join (select min(group_id) as group_id, min(SUBTYPE_GROUP_ID) as SUBTYPE_GROUP_ID, group_name from sub_type_codes where type_id in ('DX','DXX') group by group_name) as b on (a.group_name=b.group_name)
set a.group_id=b.group_id , a.SUBTYPE_GROUP_ID=b.SUBTYPE_GROUP_ID;


  update sub_type_codes a
join (select min(group_id) as group_id, min(SUBTYPE_GROUP_ID) as SUBTYPE_GROUP_ID, group_name from sub_type_codes where type_id in ('PX','PXX') group by group_name) as b on (a.group_name=b.group_name)
set a.group_id=b.group_id , a.SUBTYPE_GROUP_ID=b.SUBTYPE_GROUP_ID;
 
drop table if exists sub_type_codes_consolidated;
create table sub_type_codes_consolidated as
SELECT
sub1.EPISODE_ID,
sub1.EPISODE_NAME,
sub1.CODE_VALUE,
sub1.TYPE_ID,
sub1.GROUP_ID,
sub1.GROUP_NAME,
case when sub1.TYPE_ID='PX' and sub2.SUBTYPE_GROUP_ID is not null then sub2.SUBTYPE_GROUP_ID else sub1.SUBTYPE_GROUP_ID end as SUBTYPE_GROUP_ID
from sub_type_codes as sub1
left join sub_type_codes as sub2
on sub1.group_name=sub2.group_name and sub1.EPISODE_ID=sub2.EPISODE_ID and sub2.TYPE_ID<>'PX' 
group by sub1.EPISODE_ID,
sub1.EPISODE_NAME,
sub1.CODE_VALUE,
sub1.TYPE_ID,
sub1.GROUP_ID,
sub1.GROUP_NAME,
sub1.SUBTYPE_GROUP_ID
;
 

